import React, { useEffect, useState } from 'react';
import { fetchStore, updateStore } from './Fetch';
import Nav from './NavBar';
import ShowCard from './Card';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import TextField from '@mui/material/TextField';

// set the style of the new presentation model
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #888888',
  boxShadow: 24,
  p: 4,
}

/**
 * Dashboard component render the dashboard page
 */
function Dashboard () {
  const backgroundColor = '#f0f1f2'
  const [open, openModal, closeModal] = useModal()
  const [name, setName] = useState('')

  // get the presentation info
  const [presentation, setPresentation] = useState({})
  const getPrestationData = async () => {
    try {
      const data = await fetchStore()
      setPresentation(data.store)
      console.log(data.store)
      console.log(presentation)
    } catch (error) {
      alert(error)
    }
  };

  useEffect(() => {
    getPrestationData()
  }, [])

  // create new presentation
  const handleCreate = async (name) => {
    if (!name.trim()) {
      alert('Name required!')
      return;
    }
    try {
      const newID = Date.now()
      const updatedPresentation = {
        ...presentation,
        [newID]: { Title: name, Slides: { p1: {} } }
      };
      const putData = await updateStore(updatedPresentation)
      if (putData) {
        closeModal()
        await getPrestationData()
        setName('')
      }
    } catch (error) {
      alert(error)
    }
  }

  const handleCancel = () => {
    closeModal()
    setName('')
  }
  return (
    <div style={{ backgroundColor, minHeight: '100vh' }}>
    <Nav/>
    <Box sx={{ display: 'flex', justifyContent: 'center' }}>
      <Button variant="contained"
      sx={{ backgroundColor: 'rgb(60, 60, 60)', marginTop: '10px' }}
      onClick={openModal}>
        New presentation
      </Button>
      <Modal my={4}
        open={open}
        onClose={closeModal}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Create a new presentation
          </Typography>
          <TextField
              label="Name of new presentation"
              variant="outlined"
              sx={{ width: '90%', my: 2 }}
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          <Button variant='outlined' sx={{ mr: 2 }} onClick={() => handleCreate(name)}>Create</Button>
          <Button variant='outlined' sx={{ mx: 2 }} onClick={() => handleCancel()}>Cancel</Button>
        </Box>
      </Modal>
    </Box>
    <ShowCard presentation={presentation} />
    </div>
  )
}

export default Dashboard;
