import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext';
import axios from 'axios';
import { Container, Box, TextField, Button, Typography, Alert, IconButton, InputAdornment, Link } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import NavBar from './NavBar';

function LoginForm() {
  const navigate = useNavigate();
  const { isAuthenticated, logout } = useAuth();
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({ username: '', password: '', general: '' });
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    // 如果用户已认证，则重定向到主页或其他页面
    if (isAuthenticated) {
      navigate('/pipeline');
    } else {
      logout(); // 确保在进入登录页时清除所有认证信息
    }
  }, [isAuthenticated, navigate, logout]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { username, password } = formData;

    try {
      const response = await axios.post('http://localhost:8000/api/login/', { username, password });
      if (response.data.auth) {
        const { access, refresh } = response.data;
        localStorage.setItem('accessToken', access);
        localStorage.setItem('refreshToken', refresh);
        login(access); // 更新认证状态
        navigate('/pipeline');
      } else {
        setErrors({ ...errors, general: 'Invalid login credentials' });
      }
    } catch (error) {
      setErrors({ ...errors, general: error.message || 'Login failed due to server error.' });
    }
  };

  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <Container component="main" maxWidth="xs">
      <NavBar />
      <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <Typography component="h1" variant="h5">Login</Typography>
        {errors.general && <Alert severity="error">{errors.general}</Alert>}
        <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <TextField
            required
            fullWidth
            id="username"
            label="Username"
            name="username"
            autoComplete="username"
            autoFocus
            value={formData.username}
            onChange={handleChange}
            error={Boolean(errors.username)}
            helperText={errors.username}
          />
          <TextField
            required
            fullWidth
            name="password"
            label="Password"
            type={showPassword ? 'text' : 'password'}
            id="password"
            autoComplete="current-password"
            value={formData.password}
            onChange={handleChange}
            error={Boolean(errors.password)}
            helperText={errors.password}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handlePasswordVisibility} onMouseDown={(e) => e.preventDefault()}>
                    {showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              )
            }}
          />
          <Button type="submit" fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
            Login
          </Button>
          <Link variant="body2" onClick={() => navigate('/passwordreset')}>
            Forgot password?
          </Link>
        </Box>
      </Box>
    </Container>
  );
}

export default LoginForm;
