import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { AppBar, Toolbar, Button, Box, Typography } from '@mui/material';
import { useAuth } from '../AuthContext'; // 导入 useAuth

function NavBar({ navBarHeight }) {
  const { isAuthenticated, logout } = useAuth(); // 获取认证状态和登出功能
  const navigate = useNavigate();
  const location = useLocation();

  const logoStyle = {
    color: 'black',
    display: 'block',
    marginRight: '20px',
    cursor: 'pointer',
    fontSize: '30px',
    fontWeight: 'bold',
    transition: 'transform 0.3s',
  };

  const linkStyle = {
    fontSize: '15px',
    textDecoration: 'none',
    color: 'inherit',
    marginRight: '30px',
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleMouseOver = (e) => {
    e.currentTarget.style.transform = 'scale(1.1)';
  };

  const handleMouseOut = (e) => {
    e.currentTarget.style.transform = 'scale(1)';
  };

  return (
    <AppBar position="fixed" sx={{ height: `${navBarHeight}px`, backgroundColor: 'white', transition: 'height 0.3s' }}>
      <Toolbar sx={{ height: '100%', display: 'flex', justifyContent: 'space-between' }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Link to="/" style={{ textDecoration: 'none' }}>
            <Typography
              style={logoStyle}
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              onClick={() => navigate('/')}

            >
              ProteoInsight
            </Typography>
          </Link>
        </Box>
        <Box>
          {isAuthenticated && location.pathname === '/pipeline' ? (
            <Button color="inherit" onClick={handleLogout}><b>Logout</b></Button>
          ) : (
            <>
              {location.pathname !== '/login' && (
                <Link to="/login" style={linkStyle}>
                  <Button sx={{ color: 'black', fontSize: '15px', padding: '10px 20px' }}><b>Login</b></Button>
                </Link>
              )}
              {location.pathname !== '/register' && (
                <Link to="/register" style={linkStyle}>
                  <Button sx={{ color: 'black', fontSize: '15px', padding: '10px 20px' }}><b>Register</b></Button>
                </Link>
              )}
            </>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default NavBar;
