import React, { useState, useEffect } from 'react';
import { fetchStore } from './Fetch';

function Canvas ({ id, currentIndex }) {
  const [content, setContent] = useState([]);

  useEffect(() => {
    fetchContent().then(setContent);
  }, [id, currentIndex]);

  const fetchContent = async () => {
    try {
      const data = await fetchStore();
      const pageID = `p${currentIndex}`;
      const slideContent = data.store[id].Slides[pageID];

      return Object.keys(slideContent).map((key) => {
        const item = slideContent[key];
        switch (item.type) {
          case 'Text':
            return (
              <textarea
                key={key}
                style={{
                  width: `${item.width}%`,
                  height: `${item.height}%`,
                  fontSize: item.fontSize,
                  color: item.color,
                  position: 'absolute',
                  left: item.xPos,
                  top: item.yPos
                }}
                defaultValue={item.text}
              />
            );
          case 'Image':
            return (
              <img
                key={key}
                src={item.src}
                alt={item.alt || 'Image description not available'}
                style={{
                  width: item.width,
                  height: item.height,
                  position: 'absolute',
                  left: item.xPos,
                  top: item.yPos
                }}
              />
            );
          default:
            return null;
        }
      });
    } catch (error) {
      console.error('Failed to fetch or process data:', error);
      return [];
    }
  };

  return (
    <div style={{ height: '80vh', width: '85vw', position: 'fixed', top: '15vh', left: '10vw' }}>
      <p>Canvas:</p>
      {content}
    </div>
  );
}

export default Canvas;
