import React, { useEffect, useState } from "react";
import { scroller } from "react-scroll";
import { useSpring, animated } from 'react-spring'; // 导入 react-spring
import backgroundImage from '../assets/background.jpg'; // 导入背景图片
import seraImage from '../assets/SERA-Platform.png';
import pie from '../assets/PIE.png';
import piwas from '../assets/PIWAS.png';
import long_covid from '../assets/long-covid.png';
import NavBar from "./NavBar";
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import { IconButton } from '@mui/material';

function Dashboard() {
  const [isVisible, setIsVisible] = useState(false);
  const [navBarHeight, setNavBarHeight] = useState(100);

  useEffect(() => {
    setIsVisible(true);

    const handleScroll = () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
      if (scrollTop > 50) {
        setNavBarHeight(60);
      } else {
        setNavBarHeight(100);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const dashboardStyle = {
    display: 'flex',
    width: '100vw',
    height: '100vh',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
    transition: 'opacity 1s ease-in-out',
    opacity: isVisible ? 1 : 0,
  };

  const backgroundStyle = {
    content: '""',
    position: 'fixed', // 固定背景
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: '#4f2479',
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    opacity: 0.6, // 设置背景图片的透明度
    zIndex: -1, // 确保背景图片在内容后面
    backgroundBlendMode: 'multiply',
  };

  const textContainerStyle = {
    position: 'absolute',
    width: '80vw',
    top: '50%', // 调整top值向上移动
    left: '50%',
    transform: 'translate(-50%, -50%)',
    textAlign: 'center',
    color: 'black',
    zIndex: 1, // 确保文本在背景图片前面
  };

  const titleStyle1 = {
    fontSize: '42px',
    fontWeight: 'bold',
    width: 'auto',
    marginBottom: '40px',
    color: 'white',
  };

  const paragraphStyle1 = {
    maxWidth: '80vw', // 设置最大宽度而不是宽度的百分比
    fontSize: '24px',
    marginTop: '20px',
    fontWeight: 'bold',
    lineHeight: '1.4',
    color: 'white',
  };

  const titleStyle = {
    color: '#3c2e5f',
    fontSize: '42px',
    fontWeight: 'bold',
    width: 'auto',
    marginBottom: '40px',
  };

  const paragraphStyle = {
    color: '#3c2e5f',
    maxWidth: '80vw', // 设置最大宽度而不是宽度的百分比
    fontSize: '24px',
    marginTop: '20px',
    fontWeight: 'bold',
    lineHeight: '1.4',
  };

  const whiteSectionStyle = {
    ...dashboardStyle,
    backgroundImage: 'linear-gradient(to right, white 0%, #E9EBFE 100%)',
    color: 'black',
  };

  const arrowButtonStyle = {
    position: 'absolute',
    bottom: '80px',
    left: '50%',
    transform: 'translateX(-50%)',
    zIndex: 1,
  };

  const handleArrowClick = (section) => {
    scroller.scrollTo(section, {
      duration: 1500, // 设置滚动持续时间为1.5秒
      delay: 0,
      smooth: 'easeInOutQuart',
    });
  };

  const fadeInProps = useSpring({
    opacity: isVisible ? 1 : 0,
    transform: isVisible ? 'translateY(0)' : 'translateY(20px)',
    config: { duration: 1000 },
  });

  return (
    <>
      <NavBar navBarHeight={navBarHeight} />
      <div style={backgroundStyle}></div>
      <animated.div className="section" id="section1" style={{ ...dashboardStyle, ...fadeInProps }}>
        <div style={textContainerStyle}>
          <div style={titleStyle1}>Welcome to ProteoInsight</div>
          <div style={paragraphStyle1}>
            An advanced platform focused on protein analysis. We use the latest PIWAS and PIE algorithms to provide you with comprehensive and accurate proteomics data analysis and insights. Whether you are a researcher, bioinformatics expert, or a professional engaged in biomedical research, ProteoInsight will be your right-hand assistant for protein research and analysis.
          </div>
        </div>
        <IconButton sx={{ color: 'white' }} onClick={() => handleArrowClick('section2')} style={arrowButtonStyle}>
          <ArrowDownwardIcon fontSize="large" />
        </IconButton>
      </animated.div>
      <animated.div className="section" id="section2" style={{ ...whiteSectionStyle, ...fadeInProps }}>
        <div style={textContainerStyle}>
          <div style={titleStyle}>Analysis Algorithm - PIWAS</div>
          <div style={paragraphStyle}>
            <b>Protein-based Immunome Wide Association Study ( PIWAS  )</b> is designed to identify antigen and epitope signals against a reference proteome by scoring and smoothing k-mers tiled onto the protein sequence.
            <br />
          </div>
          <img src={piwas} style={{ height: '40vh', marginTop: '40px' }} alt="piwas_flow_graph" />
        </div>
        <IconButton onClick={() => handleArrowClick('section3')} style={arrowButtonStyle}>
          <ArrowDownwardIcon fontSize="large" />
        </IconButton>
      </animated.div>
      <animated.div className="section" id="section3" style={{ ...whiteSectionStyle, ...fadeInProps }}>
        <div style={textContainerStyle}>
          <div style={titleStyle}>Analysis Algorithm - PIE</div>
          <div style={paragraphStyle}>
            <b>Protein-wide Identification of Epitopes ( PIE )</b> is a complementary algorithm used to locates regions within the protein sequence that exhibit strong outlier signals, identifying regions of interest based on statistical significance.
          </div>
          <img src={pie} style={{ height: '50vh', marginTop: '40px' }} alt="pie_flow_graph" />
        </div>
        <IconButton onClick={() => handleArrowClick('section4')} style={arrowButtonStyle}>
          <ArrowDownwardIcon fontSize="large" />
        </IconButton>
      </animated.div>
      <animated.div className="section" id="section4" style={{ ...whiteSectionStyle, ...fadeInProps }}>
        <div style={textContainerStyle}>
          <div style={titleStyle}>About input data</div>
          <div style={paragraphStyle}>
            The input data is come from Serum Epitope Repertoire Analysis (SERA) Platform. Combining universal chemistry with proprietary informatics, SERA assesses antibody repertoires to address research and clinical needs. For more information, visit <a href="https://serimmune.com/technology/#sera" target="_blank" rel="noopener noreferrer">Serimmune</a> website.
          </div>
          <img src={seraImage} style={{ height: '30vh', marginTop: '40px' }} alt="SERA-Platform" />
        </div>
        <IconButton onClick={() => handleArrowClick('section5')} style={arrowButtonStyle}>
          <ArrowDownwardIcon fontSize="large" />
        </IconButton>
      </animated.div>
      <animated.div className="section" id="section5" style={{ ...whiteSectionStyle, ...fadeInProps }}>
        <div style={textContainerStyle}>
          <div style={titleStyle}>Default Library</div>
          <div style={paragraphStyle}>
            The default library includes paired case/control samples from long COVID cases, which are used to study the immune response in long COVID patients. Feel free to check it out and further explore the related data.
          </div>
          <a href="https://www.ncbi.nlm.nih.gov/pmc/articles/PMC10620090/" target="_blank" rel="noopener noreferrer">
            <img src={long_covid} style={{ height: '50vh', marginTop: '40px' }} alt="long_covid_article" />
          </a>
        </div>
      </animated.div>
    </>
  );
}

export default Dashboard;
