import React, { useState } from 'react';
import NavBar from './NavBar';
import { TextField, Button, Box, Container, Typography, IconButton, InputAdornment, Alert, LinearProgress } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const checkPasswordStrength = (password) => {
  if (password.length < 8) {
    return 'weak';
  } else if (!/[A-Z]/.test(password) || !/[a-z]/.test(password) || !/[\W_]/.test(password)) {
    return 'medium';
  } else {
    return 'strong';
  }
};

const PasswordReset = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    email: '',
    verificationCode: '',
    new_password: '',
    confirm_password: '',
  });
  const [step, setStep] = useState(1);
  const [errors, setErrors] = useState({});
  const [generalError, setGeneralError] = useState('');
  const [passwordStrength, setPasswordStrength] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === 'new_password') {
      setPasswordStrength(checkPasswordStrength(value));
    }
  };

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleFieldErrors = (error) => {
    const newErrors = { email: '', verificationCode: '', new_password: '', confirm_password: '', general: '' };
    if (error.response) {
      const { message, code } = error.response.data.error;
      if (code === 'INVALID_EMAIL') {
        newErrors.email = message;
      } else if (code === 'INVALID_VERIFICATION_CODE') {
        newErrors.verificationCode = message;
      } else if (code === 'INVALID_PASSWORD_FORMAT') {
        newErrors.new_password = message;
      } else if (code === 'PASSWORDS_DO_NOT_MATCH') {
        newErrors.confirm_password = message;
      } else if (code === 'MISSING_FIELDS') {
        newErrors.general = message;
      } else {
        newErrors.general = message;
      }
    } else {
      newErrors.general = 'An error occurred while processing your request. Please try again.';
    }
    setErrors(newErrors);
  };

  const handleSendEmail = (event) => {
    event.preventDefault();
    const { email } = formData;

    setErrors({});
    setGeneralError('');

    if (!email) {
      setErrors({ email: 'Email is required' });
      return;
    }

    axios.post('http://localhost:8000/api/send-verification-code/', { email })
      .then((response) => {
        alert('Verification code sent to your email.');
        setStep(2); // Move to the next step
      })
      .catch((error) => {
        handleFieldErrors(error);
      });
  };

  const handleVerifyCode = (event) => {
    event.preventDefault();
    const { email, verificationCode } = formData;

    setErrors({});
    setGeneralError('');

    if (!verificationCode) {
      setErrors({ verificationCode: 'Verification code is required' });
      return;
    }

    axios.post('http://localhost:8000/api/verify-code/', { email, verificationCode })
      .then((response) => {
        setStep(3); // Move to the next step
      })
      .catch((error) => {
        handleFieldErrors(error);
      });
  };

  const handleResetPassword = (event) => {
    event.preventDefault();
    const { email, verificationCode, new_password, confirm_password } = formData;

    setErrors({});
    setGeneralError('');

    let isValid = true;
    const newErrors = {};

    if (!new_password) {
      newErrors.new_password = 'New password is required';
      isValid = false;
    }
    if (new_password !== confirm_password) {
      newErrors.confirm_password = 'Passwords do not match';
      isValid = false;
    }

    if (!isValid) {
      setErrors(newErrors);
      return;
    }

    axios.post('http://localhost:8000/api/reset-password/', {
      email,
      verificationCode,
      new_password,
    })
      .then((response) => {
        alert('Password reset successfully.');
        navigate('/login'); // 密码重置成功后跳转
      })
      .catch((error) => {
        handleFieldErrors(error);
      });
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            marginTop: 15,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Typography component="h1" variant="h5">
            Password Reset
          </Typography>
          {generalError && (
            <Alert severity="error" sx={{ mt: 1, mb: 1 }}>
              {generalError}
            </Alert>
          )}
          {step === 1 && (
            <Box component="form" onSubmit={handleSendEmail} sx={{ mt: 1 }}>
              <></>
              <TextField
                margin="normal"
                required
                fullWidth
                id="email"
                label="Email"
                name="email"
                autoComplete="email"
                autoFocus
                value={formData.email}
                onChange={handleChange}
                error={Boolean(errors.email)}
                helperText={errors.email}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, backgroundColor: '#6699cc' }}
              >
                Send Verification Code
              </Button>
            </Box>
          )}
          {step === 2 && (
            <Box component="form" onSubmit={handleVerifyCode} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                id="verificationCode"
                label="Verification Code"
                name="verificationCode"
                autoFocus
                value={formData.verificationCode}
                onChange={handleChange}
                error={Boolean(errors.verificationCode)}
                helperText={errors.verificationCode}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, backgroundColor: '#6699cc' }}
              >
                Verify Code
              </Button>
            </Box>
          )}
          {step === 3 && (
            <Box component="form" onSubmit={handleResetPassword} sx={{ mt: 1 }}>
              <TextField
                margin="normal"
                required
                fullWidth
                name="new_password"
                label="New Password"
                type={showPassword ? 'text' : 'password'}
                id="new_password"
                autoComplete="new-password"
                value={formData.new_password}
                onChange={handleChange}
                error={Boolean(errors.new_password)}
                helperText={errors.new_password}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowPassword}
                        edge="end"
                      >
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
              {formData.new_password && (
                <>
                  <Box display="flex" alignItems="center" sx={{ mt: 1, width: '100%' }}>
                    <Box width="33%">
                      <LinearProgress
                        variant="determinate"
                        value={passwordStrength === 'weak' ? 100 : 0}
                        sx={{
                          height: 10,
                          borderRadius: 5,
                          backgroundColor: '#e0e0e0',
                          '& .MuiLinearProgress-bar': {
                            borderRadius: 5,
                            backgroundColor: passwordStrength === 'weak' ? 'red' : '#e0e0e0',
                          },
                        }}
                      />
                    </Box>
                    <Box width="33%" sx={{ ml: 1 }}>
                      <LinearProgress
                        variant="determinate"
                        value={passwordStrength === 'medium' ? 100 : 0}
                        sx={{
                          height: 10,
                          borderRadius: 5,
                          backgroundColor: '#e0e0e0',
                          '& .MuiLinearProgress-bar': {
                            borderRadius: 5,
                            backgroundColor: passwordStrength === 'medium' ? 'orange' : '#e0e0e0',
                          },
                        }}
                      />
                    </Box>
                    <Box width="33%" sx={{ ml: 1 }}>
                      <LinearProgress
                        variant="determinate"
                        value={passwordStrength === 'strong' ? 100 : 0}
                        sx={{
                          height: 10,
                          borderRadius: 5,
                          backgroundColor: '#e0e0e0',
                          '& .MuiLinearProgress-bar': {
                            borderRadius: 5,
                            backgroundColor: passwordStrength === 'strong' ? 'green' : '#e0e0e0',
                          },
                        }}
                      />
                    </Box>
                  </Box>
                  <Box display="flex" justifyContent="space-between" width="100%" sx={{ mt: 1 }}>
                    <Typography
                      variant="body2"
                      color={passwordStrength === 'weak' ? 'red' : 'textSecondary'}
                    >
                      Weak
                    </Typography>
                    <Typography
                      variant="body2"
                      color={passwordStrength === 'medium' ? 'orange' : 'textSecondary'}
                    >
                      Medium
                    </Typography>
                    <Typography
                      variant="body2"
                      color={passwordStrength === 'strong' ? 'green' : 'textSecondary'}
                    >
                      Strong
                    </Typography>
                  </Box>
                </>
              )}
              <TextField
                margin="normal"
                required
                fullWidth
                name="confirm_password"
                label="Confirm Password"
                type={showPassword ? 'text' : 'password'}
                id="confirm_password"
                autoComplete="new-password"
                value={formData.confirm_password}
                onChange={handleChange}
                error={Boolean(errors.confirm_password)}
                helperText={errors.confirm_password}
              />
              <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2, backgroundColor: '#6699cc' }}
              >
                Reset Password
              </Button>
            </Box>
          )}
        </Box>
      </Container>
    </>
  );
}

export default PasswordReset;
