import React, { useState } from 'react';
import { Container, Box, Typography, Select, MenuItem, Button, Paper, Alert } from '@mui/material';
import NavBar from './NavBar';
import axios from 'axios';
import { useAuth } from '../AuthContext';
import ProcessedResult from './ProcessedResult';
import LoadingAnimation from './LoadingAnimation'; 
import PIWASUpload from './PIWASUpload';
import PIEUpload from './PIEUpload';

const defaultFiles = {
  caseSample: {
    'AD-001': 'AD-311',
    'AD-002': 'AD-015',
    'AD-003': 'AD-033',
    'AD-004': 'AD-055',
    'AD-006': 'AD-012',
    'AD-007': 'AD-043',
    'AD-011': 'AD-308',
    'AD-016': 'AD-005',
    'AD-022': 'AD-024',
    'AD-028': 'AD-037',
    'AD-035': 'AD-056',
    'AD-042': 'AD-301',
    'AD-044': 'AD-079',
    'AD-053': 'AD-025',
    'AD-058': 'AD-021',
    'AD-077': 'AD-013',
    'AD-302': 'AD-067',
    'AD-303': 'AD-031',
    'AD-304': 'AD-051',
    'AD-306': 'AD-040',
    'AD-309': 'AD-059',
    'AD-312': 'AD-072',
    'AD-313': 'AD-073',
    'AD-315': 'AD-052',
    'AD-316': 'AD-054',
    'AD-318': 'AD-325',
    'AD-319': 'AD-046',
    'AD-320': 'AD-029',
    'AD-322': 'AD-321',
    'AD-323': 'AD-023',
    'AD-324': 'AD-081'
  },
  proteinSequence: ['default_protein_sequence_1', 'default_protein_sequence_2'],
  piwasResult: ['default_piwas_case_result', 'default_piwas_control_result']
};

const defaultFilesPath = {
  caseSample: {
    'AD-001': {
      '5k': '/default-files/AD001-5k.csv',
      '6k': '/default-files/AD001-6k.csv'
    },
    // 其他case文件...
  },
  controlSample: {
    'AD-311': {
      '5k': '/default-files/AD311-5k.csv',
      '6k': '/default-files/AD311-6k.csv'
    },
    // 其他control文件...
  },
  proteinSequence: [
    '/default-files/P0DTC9.fasta',
    '/default-files/default_protein_sequence_2.csv'
  ],
  piwasResult: [
    '/default-files/default_piwas_case_result.csv',
    '/default-files/default_piwas_control_result.csv'
  ],
}

const Pipeline = () => {
  const { token } = useAuth(); // 从AuthContext获取Token
  const [algorithm, setAlgorithm] = useState('');
  const [files, setFiles] = useState({
    caseSample: [],
    controlSample: [],
    proteinSequence: [],
    piwasResult: [],
  });
  const [fileSources, setFileSources] = useState({
    caseControlSample: 'local',
    proteinSequence: 'local',
    piwasResult: 'local',
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');
  const [running, setRunning] = useState(false);
  const [processedFile, setProcessedFile] = useState(null);

  const handleAlgorithmChange = (event) => {
    setAlgorithm(event.target.value);
  };

  const handleFileChange = (event) => {
    const { name, files: selectedFiles } = event.target;
    const updatedFiles = { ...files, [name]: [...files[name], ...Array.from(selectedFiles)] };
    setFiles(updatedFiles);
    setUploadedFiles((prev) => {
      const filteredFiles = prev.filter(f => !files[name]?.includes(f));
      return [...filteredFiles, ...Array.from(selectedFiles)];
    });
  };

  const handleDefaultFileSelect = async (name, file) => {
    let updatedFiles = { ...files };
    const fetchFile = async (url) => {
      const response = await fetch(url);
      const blob = await response.blob();
      return new File([blob], url.split('/').pop());
    };

    try {
      if (name === 'caseSample') {
        const caseFile5kPath = defaultFilesPath.caseSample[file]?.['5k'];
        const caseFile6kPath = defaultFilesPath.caseSample[file]?.['6k'];
        const controlSampleKey = defaultFiles.caseSample[file];
        const controlFile5kPath = defaultFilesPath.controlSample[controlSampleKey]?.['5k'];
        const controlFile6kPath = defaultFilesPath.controlSample[controlSampleKey]?.['6k'];

        if (!caseFile5kPath || !caseFile6kPath || !controlFile5kPath || !controlFile6kPath) {
          throw new Error('File paths are invalid.');
        }

        const caseFile5k = await fetchFile(caseFile5kPath);
        const caseFile6k = await fetchFile(caseFile6kPath);
        const controlFile5k = await fetchFile(controlFile5kPath);
        const controlFile6k = await fetchFile(controlFile6kPath);

        updatedFiles = {
          ...files,
          caseSample: [caseFile5k, caseFile6k],
          controlSample: [controlFile5k, controlFile6k],
        };
      } else if (name === 'proteinSequence') {
        const proteinFilePath = defaultFilesPath.proteinSequence[file];
        if (!proteinFilePath) {
          throw new Error('File path is invalid.');
        }
        const proteinFile = await fetchFile(proteinFilePath);
        updatedFiles = {
          ...files,
          proteinSequence: [proteinFile],
        };
      } else if (name === 'piwasResult') {
        const piwasCaseFilePath = defaultFilesPath.piwasResult[0];
        const piwasControlFilePath = defaultFilesPath.piwasResult[1];

        if (!piwasCaseFilePath || !piwasControlFilePath) {
          throw new Error('File paths are invalid.');
        }

        const piwasCaseFile = await fetchFile(piwasCaseFilePath);
        const piwasControlFile = await fetchFile(piwasControlFilePath);
        updatedFiles = {
          ...files,
          piwasResult: [piwasCaseFile, piwasControlFile],
        };
      }
      setFiles(updatedFiles);
      setUploadedFiles((prev) => [...prev, ...updatedFiles[name]]);
    } catch (error) {
      console.error('Error in handleDefaultFileSelect:', error);
      setErrorMessage('Error selecting default file: ' + error.message);
    }
  };

  const handleFileSourceChange = (event, fileType) => {
    const value = event.target.value;
    setFileSources(prev => ({ ...prev, [fileType]: value }));

    // 清空文件列表
    if (fileType === 'caseControlSample') {
      setFiles(prev => ({ ...prev, caseSample: [], controlSample: [] }));
    } else if (fileType === 'proteinSequence') {
      setFiles(prev => ({ ...prev, proteinSequence: [] }));
    }
  };

  const downloadFile = async (filePath) => {
    try {
      const response = await axios.get('http://localhost:8000/api/download-result-file/', {
        params: { file_path: filePath },
        headers: { 'Authorization': `Bearer ${token}` },
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filePath.split('/').pop());
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      setErrorMessage('Error downloading file: ' + error.message);
    }
  };

  const handleRun = async () => {
    if (!algorithm) {
      setErrorMessage('You must select an algorithm.');
      return;
    }

    setErrorMessage('');
    setRunning(true);

    try {
      let runResponse;

      const uploadFiles = async (url, files, fieldName) => {
        const formData = new FormData();
        for (let file of files) {
          const fileData = typeof file === 'string' ? await fetchDefaultFile(file) : file;
          formData.append(fieldName, fileData);
        }
        try {
          const response = await axios.post(url, formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
              'Authorization': `Bearer ${token}` // 在请求头中包含Token
            }
          });
          console.log(`Successfully uploaded files to ${url}:`, response.data.file_paths);
          return response.data.file_paths;
        } catch (error) {
          console.error(`Error uploading files to ${url}:`, error);
          throw error;
        }
      };

      const uploadSingleFile = async (url, file, fieldName) => {
        const formData = new FormData();
        const fileData = typeof file === 'string' ? await fetchDefaultFile(file) : file;
        formData.append(fieldName, fileData);
        try {
          const response = await axios.post(url, formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
              'Authorization': `Bearer ${token}` // 在请求头中包含Token
            }
          });
          console.log(`Successfully uploaded file to ${url}:`, response.data.file_path);
          return response.data.file_path;
        } catch (error) {
          console.error(`Error uploading file to ${url}:`, error);
          throw error;
        }
      };

      const fetchDefaultFile = async (url) => {
        const response = await fetch(url);
        const blob = await response.blob();
        return new File([blob], url.split('/').pop());
      };

      if (algorithm === 'PIWAS' || algorithm === 'PIWAS+PIE') {
        const caseSampleFiles = fileSources.caseControlSample === 'local' ? files.caseSample : [defaultFilesPath.caseSample[files.caseSample[0]]['5k'], defaultFilesPath.caseSample[files.caseSample[0]]['6k']];
        const controlSampleFiles = fileSources.caseControlSample === 'local' ? files.controlSample : [defaultFilesPath.controlSample[files.controlSample[0]]['5k'], defaultFilesPath.controlSample[files.controlSample[0]]['6k']];
        const proteinFile = fileSources.proteinSequence === 'local' ? files.proteinSequence[0] : defaultFilesPath.proteinSequence[files.proteinSequence[0]];

        if (caseSampleFiles.length !== 2) {
          setErrorMessage('You must submit exactly two case sample files (5-mer and 6-mer).');
          return;
        }
        if (controlSampleFiles.length !== 2) {
          setErrorMessage('You must submit exactly two control sample files (5-mer and 6-mer).');
          return;
        }
        if (!proteinFile) {
          setErrorMessage('You must submit exactly one protein sequence file.');
          return;
        }

        const caseFilePaths = await uploadFiles('http://localhost:8000/api/upload-case-files/', caseSampleFiles, 'case_files');
        const controlFilePaths = await uploadFiles('http://localhost:8000/api/upload-control-files/', controlSampleFiles, 'control_files');
        const proteinFilePath = await uploadSingleFile('http://localhost:8000/api/upload-protein-file/', proteinFile, 'protein_file');

        if (!proteinFilePath) {
          setErrorMessage('Error uploading protein sequence file.');
          return;
        }

        const requestBody = {
          case_file_paths: {
            kmer_5: caseFilePaths.find(path => path.includes('5')),
            kmer_6: caseFilePaths.find(path => path.includes('6')),
          },
          control_file_paths: {
            kmer_5: controlFilePaths.find(path => path.includes('5')),
            kmer_6: controlFilePaths.find(path => path.includes('6')),
          },
          protein_file_path: proteinFilePath
        };

        if (algorithm === 'PIWAS+PIE') {
          requestBody.piwas_case_file_path = requestBody.case_file_paths.kmer_5;
          requestBody.piwas_control_file_path = requestBody.control_file_paths.kmer_6;
        }

        runResponse = await axios.post(`http://localhost:8000/api/run-${algorithm.toLowerCase()}-algorithm/`, requestBody, {
          headers: { 'Authorization': `Bearer ${token}` },
          timeout: 600000
        });
      } else if (algorithm === 'PIE') {
        const piwasResultFiles = fileSources.piwasResult === 'local' ? files.piwasResult : defaultFilesPath.piwasResult;
        const proteinFile = fileSources.proteinSequence === 'local' ? files.proteinSequence[0] : defaultFilesPath.proteinSequence[files.proteinSequence[0]];

        if (piwasResultFiles.length !== 2) {
          setErrorMessage('You must submit exactly two PIWAS result files (case result and control result).');
          return;
        }

        const piwasCaseFilePath = await uploadSingleFile('http://localhost:8000/api/upload-piwas-case-result/', piwasResultFiles[0], 'piwas_case_result');
        const piwasControlFilePath = await uploadSingleFile('http://localhost:8000/api/upload-piwas-control-result/', piwasResultFiles[1], 'piwas_control_result');
        const proteinFilePath = await uploadSingleFile('http://localhost:8000/api/upload-protein-file/', proteinFile, 'protein_file');

        runResponse = await axios.post('http://localhost:8000/api/run-pie-algorithm/', {
          piwas_case_file_path: piwasCaseFilePath,
          piwas_control_file_path: piwasControlFilePath,
          protein_file_path: proteinFilePath
        }, {
          headers: { 'Authorization': `Bearer ${token}` },
          timeout: 600000
        });
      }

      if (runResponse.data.status === 'success') {
        setProcessedFile(runResponse.data.file_paths);
      } else {
        setErrorMessage(runResponse.data.message);
      }
    } catch (error) {
      setErrorMessage('Error running pipeline: ' + error.message);
    } finally {
      setRunning(false);
    }
  };

  const handleReset = () => {
    setAlgorithm('');
    setFiles({
      caseSample: [],
      controlSample: [],
      proteinSequence: [],
      piwasResult: [],
    });
    setFileSources({
      caseSample: 'local',
      controlSample: 'local',
      proteinSequence: 'local',
      piwasResult: 'local',
    });
    setUploadedFiles([]);
    setErrorMessage('');
    setRunning(false);
    setProcessedFile(null);
  };

  const handleFileDelete = (fileType, index) => {
    const updatedFiles = { ...files, [fileType]: files[fileType].filter((_, i) => i !== index) };
    setFiles(updatedFiles);
    setUploadedFiles(uploadedFiles.filter((file, i) => {
      // 确保删除正确的文件类型
      const isCorrectFileType = Object.keys(files).some(key => files[key].includes(file) && key === fileType);
      return i !== index || !isCorrectFileType;
    }));
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="md">
        <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          {!running && !processedFile && (
            <Box sx={{ mt: 3, mb: 5, width: '100%' }}>
              <Paper elevation={3} sx={{ p: 3, backgroundColor: '#ffffff', mb: 5, position: 'relative', border: '1px solid #ccc' }}>
                <Typography variant="h6" sx={{ position: 'absolute', top: -20, left: 16, backgroundColor: '#ffffff', px: 1 }}>
                  Algorithm Selection
                </Typography>
                <Select
                  fullWidth
                  value={algorithm}
                  onChange={handleAlgorithmChange}
                  displayEmpty
                  sx={{ mt: 3, mb: 3, height: 50 }}
                >
                  <MenuItem value="" disabled>Select an algorithm</MenuItem>
                  <MenuItem value="PIWAS">PIWAS</MenuItem>
                  <MenuItem value="PIE">PIE</MenuItem>
                  <MenuItem value="PIWAS+PIE">PIWAS+PIE</MenuItem>
                </Select>
              </Paper>

              {algorithm && (
                <Paper elevation={3} sx={{ p: 3, backgroundColor: '#ffffff', mb: 5, position: 'relative', border: '1px solid #ccc' }}>
                  <Typography variant="h6" sx={{ position: 'absolute', top: -20, left: 16, backgroundColor: '#ffffff', px: 1 }}>
                    {algorithm === 'PIE' ? 'Upload PIWAS Results and Protein Sequence' : 'Upload Files'}
                  </Typography>
                  {algorithm === 'PIE' ? (
                    <PIEUpload
                      fileSources={fileSources}
                      handleFileSourceChange={handleFileSourceChange}
                      files={files}
                      handleFileChange={handleFileChange}
                      handleFileDelete={handleFileDelete}
                      handleDefaultFileSelect={handleDefaultFileSelect}
                      defaultFiles={defaultFiles}
                    />
                  ) : (
                    <PIWASUpload
                      fileSources={fileSources}
                      handleFileSourceChange={handleFileSourceChange}
                      files={files}
                      handleFileChange={handleFileChange}
                      handleFileDelete={handleFileDelete}
                      handleDefaultFileSelect={handleDefaultFileSelect}
                      defaultFiles={defaultFiles}
                    />
                  )}
                </Paper>
              )}

              {errorMessage && <Alert severity="error">{errorMessage}</Alert>}
              <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
                <Button variant="contained" color="primary" onClick={handleRun}>
                  Run
                </Button>
                <Button variant="outlined" color="secondary" onClick={handleReset}>
                  Reset
                </Button>
              </Box>
            </Box>
          )}

          {running && <LoadingAnimation />}

          {processedFile && (
            <ProcessedResult
              processedFile={processedFile}
              downloadFile={downloadFile}
              handleReset={handleReset}
              algorithm={algorithm}
            />
          )}
        </Box>
      </Container>
    </>
  );
};

export default Pipeline;
