import React, { useState } from 'react';
import { Modal, Box, TextField, Button, Typography, MenuItem, Select, FormControl, InputLabel } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '60vw',
  border: '2px solid #888888',
  boxShadow: 24,
  p: 4,
}

const CodeInputModal = ({ open, handleClose, handleAddCode }) => {
  const [areaSize, setAreaSize] = useState('');
  const [codeContent, setCodeContent] = useState('');
  const [fontSize, setFontSize] = useState('1.0');
  const [language, setLanguage] = useState('javascript');

  const submitCode = () => {
    handleAddCode(areaSize, codeContent, fontSize, language);
    handleClose();
  };

  return (
    <Modal open={open} onClose={handleClose} aria-labelledby="modal-title" sx={{ ...style, width: 300 }}>
      <Box sx={{ p: 4 }}>
        <Typography id="modal-title" variant="h6">Add Code Block</Typography>
        <TextField
          label="Textarea Size (e.g., 400px 300px)"
          fullWidth
          margin="normal"
          value={areaSize}
          onChange={(e) => setAreaSize(e.target.value)}
        />
        <FormControl fullWidth margin="normal">
          <InputLabel id="language-select-label">Language</InputLabel>
          <Select
            labelId="language-select-label"
            value={language}
            label="Language"
            onChange={(e) => setLanguage(e.target.value)}
          >
            <MenuItem value="javascript">JavaScript</MenuItem>
            <MenuItem value="python">Python</MenuItem>
            <MenuItem value="c">C</MenuItem>
          </Select>
        </FormControl>
        <TextField
          label="Code Content"
          multiline
          rows={4}
          fullWidth
          margin="normal"
          value={codeContent}
          onChange={(e) => setCodeContent(e.target.value)}
          InputProps={{
            style: { fontFamily: 'monospace', fontSize: `${fontSize}em` }
          }}
        />
        <TextField
          label="Font Size (em)"
          type="number"
          fullWidth
          margin="normal"
          value={fontSize}
          onChange={(e) => setFontSize(e.target.value)}
        />
        <Button onClick={submitCode} color="primary">Add Code</Button>
      </Box>
    </Modal>
  );
};

export default CodeInputModal;
