import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext'; // 确保从正确的路径导入 useAuth
import axios from 'axios';
import {
  Box,
  Button,
  Container,
  IconButton,
  InputAdornment,
  Link,
  TextField,
  Typography,
  Alert
} from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import NavBar from './NavBar';

function LoginForm() {
  const navigate = useNavigate();
  const { login } = useAuth(); // 从 AuthContext 获取 login 方法
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({ username: '', password: '', general: '' });
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const { username, password } = formData;

    if (!username || !password) {
      const newErrors = {};
      if (!username) newErrors.username = 'Username is required';
      if (!password) newErrors.password = 'Password is required';
      setErrors(newErrors);
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/api/login/', { username, password });
      if (response.data.auth) {
        login(response.data.access); // 更新认证状态
        navigate('/pipeline'); // 登录成功后跳转
      } else {
        setErrors({ general: 'Invalid login credentials' });
      }
    } catch (error) {
      setErrors({ general: error.response?.data?.detail || 'Login failed due to server error.' });
    }
  };

  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="xs">
        <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography component="h1" variant="h5">Login</Typography>
          {errors.general && <Alert severity="error">{errors.general}</Alert>}
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
              value={formData.username}
              onChange={handleChange}
              error={!!errors.username}
              helperText={errors.username}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type={showPassword ? 'text' : 'password'}
              id="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              error={!!errors.password}
              helperText={errors.password}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton onClick={handlePasswordVisibility}>
                      {showPassword ? <Visibility /> : <VisibilityOff />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            <Button type="submit" fullWidth variant="contained" sx={{ mt: 3, mb: 2 }}>
              Login
            </Button>
            <Link component="button" variant="body2" onClick={() => navigate('/passwordreset')}>
              Forgot password?
            </Link>
          </Box>
        </Box>
      </Container>
    </>
  );
}

export default LoginForm;
