import React, { useState } from 'react';
import { Modal, Box, TextField, Button, Typography, InputAdornment, IconButton } from '@mui/material';
import PhotoCamera from '@mui/icons-material/PhotoCamera';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 300,
  border: '2px solid #888888',
  boxShadow: 24,
  p: 4,
}

const ImageInputModal = ({ open, handleClose, handleAddImage }) => {
  const [imageSize, setImageSize] = useState('');
  const [imageSrc, setImageSrc] = useState('');
  const [description, setDescription] = useState('');
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImageSrc(e.target.result); // Converts image to base64
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <Modal open={open} onClose={handleClose} aria-labelledby="modal-title" sx={style}>
      <Box sx={{ p: 4 }}>
        <Typography id="modal-title" variant="h6">Add Image</Typography>
        <TextField
          label="Image Size (e.g., 200px 100px)"
          fullWidth
          margin="normal"
          value={imageSize}
          onChange={(e) => setImageSize(e.target.value)}
        />
        <TextField
          label="Image Description (alt text)"
          fullWidth
          margin="normal"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <TextField
          label="Image URL"
          fullWidth
          margin="normal"
          value={imageSrc}
          onChange={(e) => setImageSrc(e.target.value)}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  aria-label="upload picture"
                  component="label"
                >
                  <input
                    hidden
                    accept="image/*"
                    type="file"
                    onChange={handleImageUpload}
                  />
                  <PhotoCamera />
                </IconButton>
              </InputAdornment>
            ),
          }}
        />
        <Button onClick={() => handleAddImage(imageSize, imageSrc, description)} color="primary">
          Add Image
        </Button>
      </Box>
    </Modal>
  );
};

export default ImageInputModal;
