import React, { useState } from 'react';
import NavBar from './NavBar';
import { TextField, Button, Box, Container, Typography, MenuItem, IconButton, InputAdornment } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const securityQuestions = [
  { id: 1, text: 'What was your childhood nickname?' },
  { id: 2, text: 'What is the name of your favorite childhood friend?' },
  { id: 3, text: 'What was your favorite place to visit as a child?' },
  { id: 4, text: 'What was your dream job as a child?' },
];

const checkPasswordStrength = (password) => {
  if (password.length < 8) {
    return 'weak';
  } else if (!/[A-Z]/.test(password) || !/[a-z]/.test(password) || !/[\W_]/.test(password)) {
    return 'medium';
  } else {
    return 'strong';
  }
};

const PasswordReset = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    question_id: '',
    answer: '',
    new_password: '',
  });
  const [errors, setErrors] = useState({});
  const [generalError, setGeneralError] = useState('');
  const [passwordStrength, setPasswordStrength] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === 'new_password') {
      setPasswordStrength(checkPasswordStrength(value));
    }
  };

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { username, question_id, answer, new_password } = formData;

    // 清除所有错误提示
    setErrors({});
    setGeneralError('');

    let isValid = true;
    const newErrors = {};

    if (!username) {
      newErrors.username = 'Username is required';
      isValid = false;
    }
    if (!question_id) {
      newErrors.question_id = 'Security question is required';
      isValid = false;
    }
    if (!answer) {
      newErrors.answer = 'Answer is required';
      isValid = false;
    }
    if (!new_password) {
      newErrors.new_password = 'New password is required';
      isValid = false;
    }

    if (!isValid) {
      setErrors(newErrors);
      return;
    }

    axios.post('http://localhost:8000/api/reset-password/', {
      username,
      question_id,
      answer,
      new_password,
    })
      .then((response) => {
        alert('Password reset successfully.');
        navigate('/login'); // 密码重置成功后跳转
      })
      .catch((error) => {
        if (error.response && error.response.data.message) {
          setGeneralError(error.response.data.message);
        } else {
          setGeneralError('Failed to reset password, please try again.');
        }
        console.error('Error:', error);
      });
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            marginTop: 15,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Typography component="h1" variant="h5">
            Password Reset
          </Typography>
          {generalError && (
            <Typography color="error" sx={{ mt: 1, mb: 1 }}>
              {generalError}
            </Typography>
          )}
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
              value={formData.username}
              onChange={handleChange}
              error={Boolean(errors.username)}
              helperText={errors.username}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              id="securityQuestion"
              select
              label="Security Question"
              name="question_id"
              autoComplete="security-question"
              value={formData.question_id}
              onChange={handleChange}
              error={Boolean(errors.question_id)}
              helperText={errors.question_id}
            >
              {securityQuestions.map((question) => (
                <MenuItem key={question.id} value={question.id}>
                  {question.text}
                </MenuItem>
              ))}
            </TextField>
            <TextField
              margin="normal"
              required
              fullWidth
              id="answer"
              label="Answer"
              name="answer"
              autoComplete="answer"
              value={formData.answer}
              onChange={handleChange}
              error={Boolean(errors.answer)}
              helperText={errors.answer}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="new_password"
              label="New Password"
              type={showPassword ? 'text' : 'password'}
              id="new_password"
              autoComplete="new-password"
              value={formData.new_password}
              onChange={handleChange}
              error={Boolean(errors.new_password)}
              helperText={errors.new_password}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      aria-label="toggle password visibility"
                      onClick={handleClickShowPassword}
                      edge="end"
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                ),
              }}
            />
            {formData.new_password && (
              <Typography
                variant="body2"
                color={passwordStrength === 'weak' ? 'red' : passwordStrength === 'medium' ? 'orange' : 'green'}
                sx={{ mt: 1 }}
              >
                {passwordStrength === 'weak' ? 'Weak' : passwordStrength === 'medium' ? 'Medium' : 'Strong'}
              </Typography>
            )}
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2, backgroundColor: '#6699cc' }}
            >
              Reset Password
            </Button>
          </Box>
        </Box>
      </Container>
    </>
  );
};

export default PasswordReset;
