import React, { useState } from 'react';
import { Modal, Box, TextField, Button, Typography, Switch, FormControlLabel } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  maxWidth: 350,
  border: '2px solid #888888',
  p: 2,
}

const VideoInputModal = ({ open, handleClose, handleAddVideo }) => {
  const [videoSize, setVideoSize] = useState('');
  const [videoUrl, setVideoUrl] = useState('');
  const [autoPlay, setAutoPlay] = useState(false);

  const submitVideo = () => {
    handleAddVideo(videoSize, videoUrl, autoPlay);
    handleClose();
  };

  return (
    <Modal open={open} onClose={handleClose} aria-labelledby="modal-title" sx={{ ...style, width: 300 }}>
      <Box sx={{ p: 4 }}>
        <Typography id="modal-title" variant="h6">Add Video</Typography>
        <TextField
          label="Video Size (e.g., 400px 300px)"
          fullWidth
          margin="normal"
          value={videoSize}
          onChange={(e) => setVideoSize(e.target.value)}
        />
        <TextField
          label="YouTube Video URL"
          fullWidth
          margin="normal"
          value={videoUrl}
          onChange={(e) => setVideoUrl(e.target.value)}
        />
        <FormControlLabel
          control={<Switch checked={autoPlay} onChange={(e) => setAutoPlay(e.target.checked)} />}
          label="Auto-play"
        />
        <Button onClick={submitVideo} color="primary">Add Video</Button>
      </Box>
    </Modal>
  );
};

export default VideoInputModal;
