import React, { useState } from 'react';
import { Container, Box, Button, Paper, Typography } from '@mui/material';
import NavBar from './NavBar';
import FileUpload from './FileUpload';

const Pipeline = () => {
  const [files, setFiles] = useState({
    caseSample: [],
    controlSample: [],
    proteinSequence: [],
  });

  const handleFileChange = (eventOrFileType, file = null, isDelete = false) => {
    const name = typeof eventOrFileType === 'string' ? eventOrFileType : eventOrFileType.target.name;
    const selectedFiles = file ? [file] : Array.from(eventOrFileType.target.files);
    setFiles(prevFiles => {
      if (isDelete) {
        return { ...prevFiles, [name]: prevFiles[name].filter(f => f !== file) };
      } else {
        return { ...prevFiles, [name]: [...prevFiles[name], ...selectedFiles] };
      }
    });
  };

  const handleSubmit = () => {
    const formData = new FormData();
    Object.keys(files).forEach(key => {
      files[key].forEach(file => {
        formData.append(key, file);
      });
    });
    fetch('/api/upload', {
      method: 'POST',
      body: formData,
    }).then(response => response.json())
      .then(data => console.log(data))
      .catch(error => console.error(error));
  };

  return (
    <>
      <NavBar />
      <Container>
        <Box mt={4}>
          <Paper elevation={3}>
            <Typography variant="h6" gutterBottom>
              Upload Files
            </Typography>
            <FileUpload fileType="caseSample" handleFileChange={handleFileChange} files={files} />
            <FileUpload fileType="controlSample" handleFileChange={handleFileChange} files={files} />
            <FileUpload fileType="proteinSequence" handleFileChange={handleFileChange} files={files} />
            <Button variant="contained" onClick={handleSubmit}>Submit</Button>
          </Paper>
        </Box>
      </Container>
    </>
  );
};

export default Pipeline;
