import React, { useState } from 'react';
import { Modal, Box, TextField, Button, Typography } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '50vw',
  height: '55vh',
  backgroundColor: 'white',
  p: 2,
}

const TextInputModal = ({ open, handleClose, handleSubmit }) => {
  const [width, setWidth] = useState('')
  const [height, setHeight] = useState('')
  const [text, setText] = useState('')
  const [fontSize, setFontSize] = useState('1.0')
  const [color, setColor] = useState('#000000')

  const onSubmit = () => {
    handleSubmit(width, height, text, fontSize, color);
    handleClose()
  };

  return (
    <Modal open={open} onClose={handleClose} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description" sx={style}>
      <Box>
        <Typography id='modal-title' variant='h5'>Add Text</Typography>
        <TextField
          label='Width(%)'
          variant='outlined'
          margin='normal'
          size='small'
          fullWidth
          value={width}
          onChange={(e) => setWidth(e.target.value)}
        />
        <TextField
          label='Hight(%)'
          variant='outlined'
          margin='normal'
          size='small'
          fullWidth
          value={height}
          onChange={(e) => setHeight(e.target.value)}
        />
        <TextField
          label='Text Content'
          variant='outlined'
          size='small'
          fullWidth
          margin='normal'
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <TextField
          label='Font Size (em)'
          type='number'
          variant='outlined'
          size='small'
          fullWidth
          margin='normal'
          value={fontSize}
          onChange={(e) => setFontSize(e.target.value)}
        />
        <TextField
          label='Text Color'
          type='color'
          variant='outlined'
          size='small'
          fullWidth
          margin='normal'
          value={color}
          onChange={(e) => setColor(e.target.value)}
        />
        <Button onClick={onSubmit} variant="contained" sx={{ color: 'white', backgroundColor: 'rgb(60,60,60)' }}>
          Add Text
        </Button>
        <Button onClick={handleClose} sx={{ color: 'white', backgroundColor: 'rgb(60,60,60)', ml: '10px' }} variant="contained">
          Cancel
        </Button>
      </Box>
    </Modal>
  )
}

export default TextInputModal;
