import React, { useState } from 'react';
import { Modal, Box, Button, Typography, TextField, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '60vw',
  bgcolor: 'grey.300',
  border: '2px solid #888888',
  p: 2,
}

const BackgroundPickerModal = ({ open, handleClose, handleApplyBackground }) => {
  const [backgroundType, setBackgroundType] = useState('solid'); // 'solid' or 'gradient'
  const [color1, setColor1] = useState('#FFFFFF');
  const [color2, setColor2] = useState('#000000');
  const [gradientDirection, setGradientDirection] = useState('to right'); // or 'to bottom'

  const submitBackground = () => {
    const background = backgroundType === 'solid' ? color1 : `linear-gradient(${gradientDirection}, ${color1}, ${color2})`;
    handleApplyBackground(background);
    handleClose();
  };

  return (
    <Modal open={open} onClose={handleClose} aria-labelledby="modal-title" sx={{ ...style, width: '50vw' }}>
      <Box sx={{ p: 4 }}>
        <Typography id="modal-title" variant="h6">Pick Background</Typography>
        <FormControl component="fieldset">
          <FormLabel component="legend">Background Type</FormLabel>
          <RadioGroup
            row
            aria-label="background type"
            name="backgroundType"
            value={backgroundType}
            onChange={(e) => setBackgroundType(e.target.value)}
          >
            <FormControlLabel value="solid" control={<Radio />} label="Solid Color" />
            <FormControlLabel value="gradient" control={<Radio />} label="Gradient" />
          </RadioGroup>
        </FormControl>
        <TextField
          label="Primary Color"
          type="color"
          fullWidth
          margin="normal"
          value={color1}
          onChange={(e) => setColor1(e.target.value)}
        />
        {backgroundType === 'gradient' && (
          <>
            <TextField
              label="Secondary Color"
              type="color"
              fullWidth
              margin="normal"
              value={color2}
              onChange={(e) => setColor2(e.target.value)}
            />
            <TextField
              label="Gradient Direction"
              select
              fullWidth
              margin="normal"
              value={gradientDirection}
              onChange={(e) => setGradientDirection(e.target.value)}
              SelectProps={{
                native: true,
              }}
            >
              <option value="to right">Left to Right</option>
              <option value="to bottom">Top to Bottom</option>
            </TextField>
          </>
        )}
        <Button onClick={submitBackground} color="primary">Apply Background</Button>
      </Box>
    </Modal>
  );
};

export default BackgroundPickerModal;
