import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchStore, updateStore } from './Fetch';
import Nav from './NavBar';
import useModal from './useModel';
import CanvasTool from './CanvasTool';
import { Box, IconButton, Tooltip, Typography, Button, TextField, Modal } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import Canvas from './Canvas';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #888888',
  boxShadow: 24,
  p: 4,
}

function Deck () {
  const { id } = useParams()
  const [title, setTitle] = useState('')
  const [currentIndex, setCurrentIndex] = useState(1)
  const [countSlide, setCountSlide] = useState(1)
  const [singlePresentation, setSinglePresentation] = useState({})

  // get single presentation info
  const getSinglePrestation = async () => {
    try {
      const response = await fetchStore()
      const data = response.store[id]
      setSinglePresentation(data)
      setTitle(data.Title)
      const slides = Object.keys(data.Slides)
      setCountSlide(slides.length)
    } catch (error) {
      alert(error)
    }
  };

  useEffect(() => {
    getSinglePrestation()
  }, [id])

  // back to dashboard
  const navigate = useNavigate();
  const toDashboard = () => {
    navigate('/dashboard')
  }

  // delete presentation
  const deletePresentation = async () => {
    const prompt = window.confirm('Are you sure?')
    if (prompt) {
      try {
        const data = await fetchStore()
        if (data.store) {
          delete data.store[id]
          try {
            await updateStore(data.store)
            navigate('/dashboard')
          } catch (e) {
            alert(e)
          }
        }
      } catch (e) {
        alert(e)
      }
    }
  }

  // add slide
  const addSlide = async () => {
    try {
      const response = await fetchStore()
      const newSlideKey = `p${Object.keys(singlePresentation.Slides).length + 1}`
      response.store[id].Slides[newSlideKey] = {}
      try {
        await updateStore(response.store)
        setSinglePresentation(response.store[id])
        setCountSlide(prevCount => prevCount + 1)
      } catch (e) {
        alert(e)
      }
    } catch (e) {
      alert(e)
    }
  }

  // delete slide
  const deleteSlide = async () => {
    if (countSlide < 2) {
      alert('Cannot delete the only slide')
    } else {
      try {
        const response = await fetchStore()
        const pageID = `p${currentIndex}`
        delete response.store[id].Slides[pageID]
        const remainingKeys = Object.keys(response.store[id].Slides)
        const renamedSlides = {}
        remainingKeys.forEach((key, index) => {
          renamedSlides[`p${index + 1}`] = response.store[id].Slides[key];
        })
        response.store[id].Slides = renamedSlides
        try {
          await updateStore(response.store)
          setSinglePresentation(response.store[id])
          setCountSlide(Object.keys(response.store[id].Slides).length)
          if (currentIndex !== 1) {
            setCurrentIndex(index => index - 1)
          }
          console.log(response)
          console.log(currentIndex)
        } catch (e) {
          alert(e)
        }
      } catch (e) {
        alert(e)
      }
    }
  }

  // change title
  const [open, openModal, closeModal] = useModal()
  const [rename, setRename] = useState('')
  const handleRename = async (rename) => {
    if (!rename.trim()) {
      alert('New name required!')
      return;
    }
    try {
      const data = await fetchStore();
      if (data.store) {
        data.store[id].Title = rename
        try {
          const putData = await updateStore(data.store)
          if (putData) {
            setTitle(rename)
            closeModal()
          }
        } catch (error) {
          alert(error)
        }
      }
    } catch (error) {
      alert(error)
    }
  }

  // move silde
  const handlePrevious = () => {
    setCurrentIndex(index => index > 1 ? index - 1 : index)
    console.log(currentIndex)
  };

  const handleNext = () => {
    setCurrentIndex(index => index < countSlide ? index + 1 : index)
    console.log(currentIndex);
  };

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowLeft') {
        handlePrevious();
      } else if (event.key === 'ArrowRight') {
        handleNext();
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [countSlide, currentIndex]);

  return (
    <div style={{ height: '100vh', width: '100vw' }}>
      <Nav/>
      <div
      style={{
        display: 'flex',
        flexDirection: 'row',
      }}>
        <Box sx={{
          width: '5vw',
          height: '92vh',
          backgroundColor: 'rgb(60,60,60)',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-evenly',
        }}
      >
        <Tooltip title='Back to dashboard' arrow>
          <IconButton onClick={toDashboard} sx={{ color: 'white' }}>
            <ArrowBackIosIcon sx={{ fontSize: '1em' }}/>
          </IconButton>
        </Tooltip>
        <Tooltip title='Detele presentation' arrow>
          <IconButton sx={{ color: 'white' }} onClick={deletePresentation}>
            <DeleteIcon sx={{ fontSize: '1em' }}/>
          </IconButton>
        </Tooltip>
        <Tooltip title='Add new slide' arrow>
          <IconButton sx={{ color: 'white' }} onClick={addSlide}>
            <AddIcon sx={{ fontSize: '1em' }}/>
          </IconButton>
        </Tooltip>
        <Tooltip title='Detele current slide' arrow>
          <IconButton sx={{ color: 'white' }} onClick={deleteSlide}>
            <RemoveIcon sx={{ fontSize: '1em' }}/>
          </IconButton>
        </Tooltip>
      </Box>
        <div style={{ flexGrow: '1', textAlign: 'center', position: 'relative' }}>
          <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: 'white', width: '100%', }}>
            <Typography variant="h5" sx={{ color: 'rgb(60,60,60)' }} gutterBottom>
              {title}
            </Typography>
            <Tooltip title='Edit title' arrow>
              <IconButton sx={{ color: 'rgb(60,60,60)' }} onClick={openModal}>
                <EditIcon sx={{ fontSize: '0.8em' }}/>
              </IconButton>
            </Tooltip>
            <Modal my={4}
              open={open}
              onClose={closeModal}
              aria-labelledby="modal-modal-title"
              aria-describedby="modal-modal-description"
            >
              <Box sx={style}>
                <TextField
                    label="Title of presentation"
                    variant="outlined"
                    sx={{ width: '90%', my: 2 }}
                    value={rename}
                    onChange={(e) => setRename(e.target.value)}
                    required
                  />
                <Button variant='outlined' sx={{ mr: 2 }} onClick={() => handleRename(rename)}>Rename</Button>
                <Button variant='outlined' sx={{ mx: 2 }} onClick={closeModal}>Cancel</Button>
              </Box>
            </Modal>
          </div>
          <Canvas id={id} currentIndex={currentIndex}/>
          <p
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            width: '50px',
            height: '50px',
            fontSize: '1em',
            fontWeight: 'bold',
            position: 'absolute',
            bottom: '0px',
            left: '0px',
            margin: '0',
          }}>1</p>
          {countSlide > 1 && (
            <div style={{ display: 'flex', position: 'absolute', bottom: '25px', left: '45vw', }}>
              {currentIndex > 1 && (
                <Tooltip title='Previous slide'>
                  <IconButton onClick={handlePrevious}>
                    <ArrowBackIcon sx={{ fontSize: '2em' }}/>
                  </IconButton>
                </Tooltip>
              )}
              {currentIndex < countSlide && (
                <Tooltip title='Next slide'>
                  <IconButton onClick={handleNext}>
                    <ArrowForwardIcon sx={{ fontSize: '2em' }}/>
                  </IconButton>
                </Tooltip>
              )}
            </div>
          )}
          <CanvasTool id={id} currentIndex={currentIndex} style={{ position: 'fixed', bottom: '80px', right: '50px' }}/>
        </div>
      </div>
    </div>
  )
}

export default Deck;
