import React, { useState } from 'react';
import { Box, SpeedDial, SpeedDialIcon, SpeedDialAction } from '@mui/material';
import { fetchStore, updateStore } from './Fetch';
import TextInputModal from './AddText';
import ImageInputModal from './AddImage';
import VideoInputModal from './AddVideo';
import CodeInputModal from './AddCode';
import BackgroundPickerModal from './AddTheme';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import ImageIcon from '@mui/icons-material/Image';
import OndemandVideoIcon from '@mui/icons-material/OndemandVideo';
import CodeIcon from '@mui/icons-material/Code';
import WallpaperIcon from '@mui/icons-material/Wallpaper';

const actions = [
  { icon: <TextFieldsIcon />, name: 'Text' },
  { icon: <ImageIcon />, name: 'Image' },
  { icon: <OndemandVideoIcon />, name: 'Video' },
  { icon: <CodeIcon />, name: 'Code' },
  { icon: <WallpaperIcon />, name: 'Background' },
];
function CanvasTool ({ id, currentIndex, style }) {
  const [open, setOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState({
    text: false,
    image: false,
    video: false,
    code: false,
    background: false
  });

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleOpenModal = (modalType) => {
    setModalOpen({ ...modalOpen, [modalType]: true });
    handleClose();
  };

  const handleCloseModal = (modalType) => {
    setModalOpen({ ...modalOpen, [modalType]: false });
  };

  const handleTextModal = async (width, height, text, fontSize, color) => {
    try {
      console.log(width, height, text, fontSize, color)
      const data = await fetchStore()
      const pageID = `p${currentIndex}`
      const content = { Text: { Width: width, Hight: height, Text: text, FontSize: fontSize, Color: color, xPos: 0, yPos: 0 } }
      data.store[id].Slides[pageID] = content
      try {
        await updateStore(data.store)
        console.log(data)
      } catch (e) {
        alert(e)
      }
    } catch (e) {
      alert(e)
    }
  }

  return (
    <>
      <Box sx={{ height: 330, position: 'fixed', bottom: '50px', right: '20px', ...style }}>
        <SpeedDial
          ariaLabel="Tools"
          sx={{ position: 'absolute', bottom: 10, right: 16 }}
          icon={<SpeedDialIcon />}
          onClose={handleClose}
          onOpen={handleOpen}
          open={open}
        >
          {actions.map((action) => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              tooltipTitle={action.name}
              onClick={() => handleOpenModal(action.name.toLowerCase())}
            />
          ))}
        </SpeedDial>
      </Box>
      <TextInputModal open={modalOpen.text} handleClose={() => handleCloseModal('text')} handleSubmit={handleTextModal} />
      <ImageInputModal open={modalOpen.image} handleClose={() => handleCloseModal('image')} />
      <VideoInputModal open={modalOpen.video} handleClose={() => handleCloseModal('video')} />
      <CodeInputModal open={modalOpen.code} handleClose={() => handleCloseModal('code')} />
      <BackgroundPickerModal open={modalOpen.background} handleClose={() => handleCloseModal('background')} />
    </>
  );
}

export default CanvasTool;
