import React, { useState } from 'react';
import { Container, Box, Typography, MenuItem, Select, Button, List, ListItem, ListItemText, IconButton, Grid, Radio, RadioGroup, FormControlLabel, Paper, Divider } from '@mui/material';
import { Visibility, Delete } from '@mui/icons-material';
import NavBar from './NavBar';

const defaultFiles = {
  caseSample: ['default_case_sample_1', 'default_case_sample_2'],
  controlSample: ['default_control_sample_1', 'default_control_sample_2'],
  proteinSequence: ['default_protein_sequence_1', 'default_protein_sequence_2']
};

const Pipeline = () => {
  const [algorithm, setAlgorithm] = useState('');
  const [files, setFiles] = useState({
    caseSample: null,
    controlSample: null,
    proteinSequence: null,
  });
  const [fileSources, setFileSources] = useState({
    caseSample: 'local',
    controlSample: 'local',
    proteinSequence: 'local',
  });
  const [uploadedFiles, setUploadedFiles] = useState([]);

  const handleAlgorithmChange = (event) => {
    setAlgorithm(event.target.value);
  };

  const handleFileChange = (event) => {
    const { name, files: selectedFiles } = event.target;
    setFiles({ ...files, [name]: selectedFiles[0] });
    setUploadedFiles((prev) => [...prev, selectedFiles[0]]);
  };

  const handleDefaultFileSelect = (name, file) => {
    setFiles({ ...files, [name]: file });
    setUploadedFiles((prev) => [...prev, { name: file, size: null }]);
  };

  const handleFileSourceChange = (event, fileType) => {
    setFileSources({ ...fileSources, [fileType]: event.target.value });
    setFiles({ ...files, [fileType]: null }); // Reset the selected file
  };

  const handleRun = () => {
    // 在这里处理运行逻辑
    console.log('Running with:', algorithm, files);
  };

  const handleReset = () => {
    setAlgorithm('');
    setFiles({
      caseSample: null,
      controlSample: null,
      proteinSequence: null,
    });
    setFileSources({
      caseSample: 'local',
      controlSample: 'local',
      proteinSequence: 'local',
    });
    setUploadedFiles([]);
  };

  const handleFileDelete = (file) => {
    setUploadedFiles(uploadedFiles.filter((f) => f !== file));
  };

  const handleFileView = (file) => {
    alert(`Viewing file: ${file.name || file}`);
  };

  const formatFileSize = (size) => {
    if (size === null) return '';
    const i = Math.floor(Math.log(size) / Math.log(1024));
    return `${(size / Math.pow(1024, i)).toFixed(2)} ${['B', 'KB', 'MB', 'GB', 'TB'][i]}`;
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="md">
        <Box sx={{ marginTop: 8, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography component="h1" variant="h5">
            Pipeline
          </Typography>
          <Box sx={{ mt: 3, width: '100%' }}>
            <Paper elevation={3} sx={{ p: 3, backgroundColor: '#f5f5f5', mb: 3, position: 'relative' }}>
              <Typography variant="h6" sx={{ position: 'absolute', top: -20, left: 16, backgroundColor: '#f5f5f5', px: 1 }}>
                Algorithm Selection
              </Typography>
              <Select
                fullWidth
                value={algorithm}
                onChange={handleAlgorithmChange}
                displayEmpty
                sx={{ mt: 3, mb: 3 }}
              >
                <MenuItem value="" disabled>Select an algorithm</MenuItem>
                <MenuItem value="PIWAS">PIWAS</MenuItem>
                <MenuItem value="PIE">PIE</MenuItem>
                <MenuItem value="PIWAS+PIE">PIWAS+PIE</MenuItem>
              </Select>

              <Typography variant="h6">Upload Files</Typography>

              {['caseSample', 'controlSample', 'proteinSequence'].map((fileType, index) => (
                <Box key={index} sx={{ mt: 2 }}>
                  <Typography>{fileType.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</Typography>
                  <Grid container spacing={2} alignItems="center">
                    <Grid item xs={6}>
                      <RadioGroup
                        row
                        name={fileType}
                        value={fileSources[fileType]}
                        onChange={(e) => handleFileSourceChange(e, fileType)}
                      >
                        <FormControlLabel value="local" control={<Radio />} label="Upload from local" />
                        <FormControlLabel value="default" control={<Radio />} label="Select default file" />
                      </RadioGroup>
                    </Grid>
                    <Grid item xs={6}>
                      {fileSources[fileType] === 'local' ? (
                        <input
                          type="file"
                          name={fileType}
                          onChange={handleFileChange}
                          style={{ display: 'block', marginTop: '10px' }}
                        />
                      ) : (
                        <Select
                          fullWidth
                          displayEmpty
                          onChange={(event) => handleDefaultFileSelect(fileType, event.target.value)}
                          value={files[fileType] || ''}
                        >
                          <MenuItem value="" disabled>Select a default file</MenuItem>
                          {defaultFiles[fileType].map((file, index) => (
                            <MenuItem key={index} value={file}>{file}</MenuItem>
                          ))}
                        </Select>
                      )}
                    </Grid>
                  </Grid>
                </Box>
              ))}
            </Paper>

            <Paper elevation={3} sx={{ p: 3, backgroundColor: '#eee', position: 'relative' }}>
              <Typography variant="h6" sx={{ position: 'absolute', top: -20, left: 16, backgroundColor: '#ddd', px: 1 }}>
                Uploaded Files
              </Typography>
              <List sx={{ mt: 3 }}>
                {uploadedFiles.map((file, index) => (
                  <React.Fragment key={index}>
                    <ListItem>
                      <ListItemText
                        primary={file.name || file}
                        secondary={file.size !== undefined ? formatFileSize(file.size) : ''}
                      />
                      <IconButton edge="end" aria-label="view" onClick={() => handleFileView(file)}>
                        <Visibility />
                      </IconButton>
                      <IconButton edge="end" aria-label="delete" onClick={() => handleFileDelete(file)}>
                        <Delete />
                      </IconButton>
                    </ListItem>
                    {index < uploadedFiles.length - 1 && <Divider />}
                  </React.Fragment>
                ))}
              </List>
            </Paper>

            <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between' }}>
              <Button variant="contained" color="primary" onClick={handleRun}>
                Run
              </Button>
              <Button variant="outlined" color="secondary" onClick={handleReset}>
                Reset
              </Button>
            </Box>
          </Box>
        </Box>
      </Container>
    </>
  );
};

export default Pipeline;
