import React from 'react';
import { useNavigate } from 'react-router-dom';

import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { CardActionArea } from '@mui/material';

function ShowCard (props) {
  const presentation = props.presentation ? Object.entries(props.presentation) : []
  console.log(presentation)
  if (presentation.length === 0) {
    return null
  }

  const navigate = useNavigate();
  const toDeck = (id) => () => {
    navigate(`/edit/${id}`);
  }

  return (
    <>
    <Box sx={{
      width: '100vw',
      padding: '10px',
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(100px, 300px))',
      gap: 2
    }}>
      {presentation.map(([key, details]) => (
        <Card key={key} sx={{ aspectRatio: '2 / 1' }}>
          <CardActionArea onClick={toDeck(key)}>
            <CardMedia
              component="img"
              sx={{ width: '100%' }}
              image={details.p1 || 'placeholder.jpg'}
              alt={details.Title}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="div" sx={{ wordBreak: 'break-word' }}>
                {details.Title || 'Untitled'}
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ wordBreak: 'break-word' }}>
                {details.description || ''}
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ wordBreak: 'break-word' }}>
                Number of Slides: {details.Slides ? Object.keys(details.Slides).length : 0}
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      ))}
    </Box>
    </>
  );
}

export default ShowCard
