import React, { useState } from "react";
import NavBar from "./NavBar";
import { TextField, Button, Box, Container, Typography, Link, IconButton, InputAdornment, Alert } from '@mui/material';
import { Visibility, VisibilityOff } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ username: '', password: '' });
  const [errors, setErrors] = useState({ username: '', password: '', general: '' });
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { username, password } = formData;

    // 清除所有错误提示
    setErrors({ username: '', password: '', general: '' });

    if (!username || !password) {
      const newErrors = {};
      if (!username) {
        newErrors.username = 'Username is required';
      }
      if (!password) {
        newErrors.password = 'Password is required';
      }
      setErrors(newErrors);
      return;
    }

    axios.post('http://localhost:8000/api/login/', { username, password })
      .then((response) => {
        if (response.data.auth) {
          navigate('/pipeline'); // 登录成功后跳转
        } else {
          setErrors({ ...errors, general: response.data.error.message });
        }
      })
      .catch((error) => {
        if (error.response && error.response.data && error.response.data.error) {
          setErrors({ ...errors, general: error.response.data.error.message || 'Login failed due to server error.' });
        } else {
          setErrors({ ...errors, general: 'Login failed due to server error.' });
        }
      });
  };

  const handlePasswordResetClick = () => {
    navigate('/passwordreset');
  };

  return (
    <>
      <NavBar />
      <Container component="main" maxWidth="xs">
        <Box
          sx={{
            marginTop: 15,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Typography component="h1" variant="h5">
            Login
          </Typography>
          {errors.general && (
            <Alert severity="error" sx={{ mt: 1, mb: 1 }}>
              {errors.general}
            </Alert>
          )}
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="username"
              label="Username"
              name="username"
              autoComplete="username"
              autoFocus
              value={formData.username}
              onChange={handleChange}
              error={Boolean(errors.username)}
              helperText={errors.username}
            />
            <TextField
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type={showPassword ? 'text' : 'password'}
              id="password"
              autoComplete="current-password"
              value={formData.password}
              onChange={handleChange}
              error={Boolean(errors.password)}
              helperText={errors.password}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <IconButton
                      onClick={() => setShowPassword(!showPassword)}
                      onMouseDown={(e) => e.preventDefault()}
                    >
                      {showPassword ? <VisibilityOff /> : <Visibility />}
                    </IconButton>
                  </InputAdornment>
                )
              }}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2, backgroundColor: '#6699cc' }}
            >
              Login
            </Button>
            <Link
              component="button"
              variant="body2"
              onClick={handlePasswordResetClick}
              sx={{ mt: 2 }}
              title="Reset your password here"
            >
              Forgot your password?
            </Link>
          </Box>
        </Box>
      </Container>
    </>
  );
}

export default LoginForm;
