import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchLogin } from './Fetch';
import Nav from './NavBar';
import { Box, Stack, TextField, Button, Typography } from '@mui/material';

/**
 *  LoginForm Component render the login page.
 */
function LoginForm () {
  const backgroundColor = '#f0f1f2'
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const navigate = useNavigate()
  /**
   * Function called when the submit button is clicked.
   * Submit the login info to backend and get the token.
   */
  const LoginSubmit = async (event) => {
    event.preventDefault();
    if (!email.trim() || !password.trim()) {
      alert('All fields must be filled out');
      return;
    }
    try {
      const data = await fetchLogin({ email, password })
      if (data.token) {
        localStorage.setItem('TOKEN', data.token)
        navigate('/dashboard');
      }
    } catch (error) {
      console.log(error)
      alert(error)
    }
  }
  return (
    <div style={{ backgroundColor, minHeight: '100vh' }}>
      <Nav/>
      <Box
        my={4}
        textAlign="center"
        gap={4}
        p={2}
      >
        <div>
          <Typography variant="h4" sx={{ color: '#888888' }} gutterBottom>
            Welcome Back!
          </Typography>
        </div>
        <div>
          <Typography variant="h6" sx={{ color: '#abaaaa' }} gutterBottom>
            Let&apos;s get you signed in
          </Typography>
        </div>
        <form onSubmit={LoginSubmit}>
          <Stack
            gap={4}
            sx={{
              width: {
                xs: '80vw',
                sm: '60vw',
                md: '30vw',
              },
              height: '40vh',
              position: 'absolute',
              top: '55%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              backgroundColor: 'white',
              padding: '2%',
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              boxShadow: '3px 3px 3px #abaaaa',
            }}
          >
            <TextField
              label="Email"
              variant="filled"
              sx={{ width: '90%' }}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <TextField
              type="password"
              label="Password"
              variant="filled"
              sx={{ width: '90%' }}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <Button
              type="submit"
              variant="contained"
              size="large"
              sx={{
                width: '30%',
                backgroundColor: '#2E2E2E',
                color: 'white',
                '&:hover': {
                  backgroundColor: '#464646',
                }
              }}
              onClick={LoginSubmit}
            >
              Submit
            </Button>
          </Stack>
        </form>
      </Box>
    </div>
  )
}

export default LoginForm;
