import React from 'react';
import { Box, Button, List, ListItem, ListItemText, IconButton } from '@mui/material';
import { Delete } from '@mui/icons-material';

const FileUpload = ({ fileType, handleFileChange, files }) => {
  return (
    <Box>
      <input
        type="file"
        name={fileType}
        onChange={handleFileChange}
        style={{ display: 'none' }}
        id={`file-input-${fileType}`}
      />
      <label htmlFor={`file-input-${fileType}`}>
        <Button variant="contained" component="span">
          Upload {fileType}
        </Button>
      </label>
      <List>
        {files[fileType].map((file, index) => (
          <ListItem key={index}>
            <ListItemText primary={file.name} />
            <IconButton onClick={() => handleFileChange(fileType, file, true)}>
              <Delete />
            </IconButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default FileUpload;
